    setStatus: function(_status){
        let alert_type = 'btn-secondary';
        switch (_status) {
            case 'PENDING':
                alert_type = 'btn-secondary';
                break;
            case 'COMPLETED':
            case 'OK':
                alert_type = 'btn-success';
                break;
            case 'NOSHOW':
            case 'C':
                alert_type = 'btn-warning';
                break;
            case 'CANCELLED':
                alert_type = 'btn-danger';
                break;
            case 'E':
                alert_type = 'btn-info';
                break;                        
            default:
                alert_type = 'btn-secondary';
                break;
        }
        return alert_type;
    },

    setPreassignment: function(_operation){
        let alert_type = 'btn-success';
        switch (_operation) {
            case 'ARRIVAL':
                alert_type = 'btn-success';
                break;
            case 'DEPARTURE':
                alert_type = 'btn-primary';
                break;
            case 'TRANSFER':
                alert_type = 'btn-info';
                break;
            default:
                alert_type = 'btn-success';
                break;
        }
        return alert_type;                
    },

